DECLARE
  R NUMERIC := 6371; -- Rayon de la Terre en km
  dLat NUMERIC;
  dLon NUMERIC;
  a NUMERIC;
  c NUMERIC;
  d NUMERIC;
BEGIN
  dLat := radians(lat2 - lat1);
  dLon := radians(lon2 - lon1);

  a := sin(dLat/2) * sin(dLat/2) + 
       cos(radians(lat1)) * cos(radians(lat2)) * 
       sin(dLon/2) * sin(dLon/2);
  c := 2 * atan2(sqrt(a), sqrt(1-a));
  d := R * c;

  RETURN d;
END;

DECLARE
  nearest_zone_id UUID;
BEGIN
  -- Pour l'instant, retourner simplement la premire zone active
  -- Dans une vraie implmentation, on calculerait la distance avec chaque zone
  SELECT id INTO nearest_zone_id
  FROM delivery_zones
  WHERE is_active = true
  LIMIT 1;

  RETURN nearest_zone_id;
END;

DECLARE
  cart_details JSONB;
BEGIN
  SELECT jsonb_build_object(
    'cart_info', (SELECT row_to_json(pc) FROM personal_carts pc WHERE pc.id = cart_id),
    'items', (
      SELECT jsonb_agg(
        jsonb_build_object(
          'id', pci.id,
          'product_id', pci.product_id,
          'quantity', pci.quantity,
          'product', (SELECT row_to_json(p) FROM products p WHERE p.id = pci.product_id)
        )
      )
      FROM personal_cart_items pci
      WHERE pci.personal_cart_id = cart_id
    )
  ) INTO cart_details;

  RETURN cart_details;
END;

DECLARE
